  // sections.forEach((section, i) => {
  //     ScrollTrigger.create({
  //       trigger: section,
  //       scroller: "#main",
  //       start: "top center",
  //       end: "bottom center",
  //       markers: true,
  //       onToggle: self => {
  //         if (self.isActive) {
  //           gsap.to(links[i], {scale: 1.3, color: "white"})
  //         } else {
  //           gsap.to(links[i], {scale: 1, color: "blue"})
  //         }
  //       }
  //     });
  // });


  //createTimeLine();




  gsap.fromTo("#DownloadIcon", { y: -8 }, { duration: 1, y: 8, ease: "circ.in", repeat: -1, yoyo: true })

  gsap.fromTo(".row-1", { x: -200, opacity: 0 }, {
    x: 0, opacity: 1, duration: 2,
    scrollTrigger: {
      trigger: ".row-1",
      toggleActions: 'restart pause restart pause'
    }
  });

  gsap.fromTo(".row-2", { x: 200, opacity: 0 }, {
    x: 0, opacity: 1, duration: 2,
    scrollTrigger: {
      trigger: ".row-2",
      toggleActions: 'restart pause restart pause'
    }
  });



  gsap.fromTo(".cloud1", { x: -500 }, {
    x: -200, duration: 4,
    scrollTrigger: {
      trigger: ".cloud1",
      toggleActions: 'restart pause restart pause'
    }
  });
  gsap.fromTo(".cloud2", { y: -200 }, {
    y: 0, duration: 4,
    scrollTrigger: {
      trigger: ".cloud2",
      toggleActions: 'restart pause restart pause'
    }
  });
  gsap.fromTo(".cloud3", { x: 500 }, {
    x: 400, duration: 4, scrollTrigger: {
      trigger: ".cloud3",
      toggleActions: 'restart pause restart pause'
    }
  });
  gsap.fromTo(".purpose-content", { opacity: 0 }, {
    opacity: 1, duration: 6, scrollTrigger: {
      trigger: ".purpose-content",
      toggleActions: 'restart pause restart pause'
    }
  });









  ScrollTrigger.create({
    trigger: ".trigger",
    pin: ".pin",
    start: 'top top',
    end: 'bottom bottom',
  });

  let headings = gsap.utils.toArray(".block");
  gsap.set(headings, { opacity: 0 })
  gsap.set(headings[0], { opacity: 1 })

  headings.forEach((heading, i) => {
    let tl = gsap.timeline({
      scrollTrigger: {
        trigger: ".trigger",
        start: () => `top+=${i * window.innerHeight} top`,
        end: () => `top+=${(i + 2) * window.innerHeight} top`,
        markers: false,
        scrub: true
      }
    });

    if (i === headings.length - 1) {
      tl.to(heading, { opacity: 1 })
        .to(heading, { opacity: 1 })
    } else {
      tl.to(heading, { opacity: 1, ease: "power4.out" })
        .to(heading, { opacity: 0, ease: "power4.in" })
    }
  });



  var pulse1 = document.getElementsByClassName("pulse1"),
    plusButton1 = document.getElementsByClassName("plus-button-1"),
    popout1 = document.getElementsByClassName("popout-1"),
    popout1Visible = false,
    pulse1Timeline = new TimelineMax({ repeat: -1, repeatDelay: 0.5 }),
    pulse2 = document.getElementsByClassName("pulse2"),
    plusButton2 = document.getElementsByClassName("plus-button-2"),
    popout2 = document.getElementsByClassName("popout-2"),
    popout2Visible = false,
    pulse2Timeline = new TimelineMax({ repeat: -1, repeatDelay: 0.5 }),
    pulse3 = document.getElementsByClassName("pulse3"),
    plusButton3 = document.getElementsByClassName("plus-button-3"),
    popout3 = document.getElementsByClassName("popout-3"),
    popout3Visible = false,
    pulse3Timeline = new TimelineMax({ repeat: -1, repeatDelay: 0.5 });

  if (window.innerWidth < 992) {
    pulse1 = document.getElementsByClassName("pulse1-mobile"),
      plusButton1 = document.getElementsByClassName("plus-button-1-mobile"),
      popout1 = document.getElementsByClassName("popout-1-mobile"),
      popout1Visible = false,
      pulse1Timeline = new TimelineMax({ repeat: -1, repeatDelay: 0.5 }),
      pulse2 = document.getElementsByClassName("pulse2-mobile"),
      plusButton2 = document.getElementsByClassName("plus-button-2-mobile"),
      popout2 = document.getElementsByClassName("popout-2-mobile"),
      popout2Visible = false,
      pulse2Timeline = new TimelineMax({ repeat: -1, repeatDelay: 0.5 }),
      pulse3 = document.getElementsByClassName("pulse3-mobile"),
      plusButton3 = document.getElementsByClassName("plus-button-3-mobile"),
      popout3 = document.getElementsByClassName("popout-3-mobile"),
      popout3Visible = false,
      pulse3Timeline = new TimelineMax({ repeat: -1, repeatDelay: 0.5 });
  }

  pulse1Timeline.fromTo(pulse1, 1,
    { transformOrigin: "center center", autoAlpha: 1, scale: 1 },
    { transformOrigin: "center center", autoAlpha: 0, scale: 2, ease: Quad.easeInOut }
  );
  pulse2Timeline.fromTo(pulse2, 1,
    { transformOrigin: "center center", autoAlpha: 1, scale: 1 },
    { transformOrigin: "center center", autoAlpha: 0, scale: 2, ease: Quad.easeInOut }
  );
  pulse3Timeline.fromTo(pulse3, 1,
    { transformOrigin: "center center", autoAlpha: 1, scale: 1 },
    { transformOrigin: "center center", autoAlpha: 0, scale: 2, ease: Quad.easeInOut }
  );

  // hover over
  plusButton1[0].addEventListener('mouseenter', function () {
    pulse1Timeline.pause(1);
  });
  plusButton2[0].addEventListener('mouseenter', function () {
    pulse2Timeline.pause(1);
  });
  plusButton3[0].addEventListener('mouseenter', function () {
    pulse3Timeline.pause(1);
  });

  // hover out
  plusButton1[0].addEventListener('mouseleave', function () {
    if (!popout1Visible) {
      pulse1Timeline.play();
    }
  });
  plusButton2[0].addEventListener('mouseleave', function () {
    if (!popout2Visible) {
      pulse2Timeline.play();
    }
  });
  plusButton3[0].addEventListener('mouseleave', function () {
    if (!popout3Visible) {
      pulse3Timeline.play();
    }
  });

  plusButton1[0].addEventListener('click', function () {
    gsap.fromTo(popout1[0], { opacity: 0 }, { opacity: 1, duration: 1 });
    popout1Visible = true;
    pulse1Timeline.pause(1);
  });
  plusButton2[0].addEventListener('click', function () {
    gsap.fromTo(popout2[0], { opacity: 0 }, { opacity: 1, duration: 1 });
    popout2Visible = true;
    pulse2Timeline.pause(1);
  });
  plusButton3[0].addEventListener('click', function () {
    gsap.fromTo(popout3[0], { opacity: 0 }, { opacity: 1, duration: 1 });
    popout3Visible = true;
    pulse3Timeline.pause(1);
  });

  window.onclick = function (e) {

    if (e.target !== plusButton1[0] && !plusButton1[0].contains(e.target) && e.target !== popout1[0] && !popout1[0].contains(e.target) &&
      e.target !== plusButton2[0] && !plusButton2[0].contains(e.target) && e.target !== popout2[0] && !popout2[0].contains(e.target) &&
      e.target !== plusButton3[0] && !plusButton3[0].contains(e.target) && e.target !== popout3[0] && !popout3[0].contains(e.target)) {

      if (popout1Visible) {
        gsap.fromTo(popout1[0], { opacity: 1 }, { opacity: 0, duration: 1 });
        popout1Visible = false;
        pulse1Timeline.play();
      }

      if (popout2Visible) {

        gsap.fromTo(popout2[0], { opacity: 1 }, { opacity: 0, duration: 1 });
        popout2Visible = false;
        pulse2Timeline.play();
      }

      if (popout3Visible) {
        gsap.fromTo(popout3[0], { opacity: 1 }, { opacity: 0, duration: 1 });
        popout3Visible = false;
        pulse3Timeline.play();
      }
    }

  }

}
